                }
            }

            $obj = [System.Collections.ArrayList]@()
            $null = $obj.Add([PSCustomObject]@{
                    SA             = $storageAccount
                    SAUsedCapacity = $usedCapacity
                })
            $null = $script:storageAccounts.Add($obj)
        }
    }

